    def start_requests(self):
        # print(len(self.abbr_full_dict)) # 892
        # print(len(self.code_abbr_full_dict))    # 3328
        for code, abbr_full in self.code_abbr_full_dict.items():
            abbr, full = abbr_full
            if abbr in full:     # 对于简称包含在全称内的，应使用简称进行检索
                param = {}
                param["当事人"] = abbr
                param = self.join_param(param)
                yield self.yield_formrequest(param, 1, code, "abbr_single")
            else:   # 对于简称不包含在全称内的情况：应采用全称简称都抓取然后去重的方式（并集）
                param = {}
                param["当事人"] = abbr
                param = self.join_param(param)
                yield self.yield_formrequest(param, 1, code, "abbr")

                param = {}
                param["当事人"] = full
                param = self.join_param(param)
                yield self.yield_formrequest(param, 1, code, "full")

    def yield_formrequest(self, param, index, code, category):
        """
        :param param: "POST" parameters
        :param index: page number
        :param code: company code
        :param category: "abbr"使用company_code对应公司的简称作为过滤条件; "full"使用company_code对应公司的全称作为过滤条件
        :return: 
        """
        post_data = {
            # "Param": "案件类型:刑事案件,法院层级:高级法院",
            "Param": param,
            "Index": repr(index),
            "Page": repr(self.cases_per_page),
            "Order": "法院层级",
            "Direction": "asc",
        }

        data = copy.deepcopy(post_data)
        data["case_parties"] = code  # parties: 当事人
        data["abbr_full_category"] = category   # 使用全称还是简称, 标志位

        return scrapy.FormRequest(url=self.url, formdata=post_data, callback=lambda response: self.parse(response, data), dont_filter=True)   # 关闭URL去重(有些url请求不成功，需要重新yield。如果打开URL去重, 这些请求无法成功?)

    def parse(self, response, data):
        """
        先按序请求各个start_url， 然后才会进入到parse中(可能是异步处理的，当start_urls比较多时，可能先进入parse? 待确定, 内部实现细节和工作原理)
        """
        body = urllib.parse.unquote_plus(response.request.body.decode("utf-8"), encoding="utf-8")
        # body: "Param=裁判日期:2016-02-07 TO 2016-02-07,案件类型:刑事案件&Index=1&Page=20&Order=法院层级&Direction=asc"
        print("body:", body)
        print("data:", data)

        post_data = copy.deepcopy(data)
        del post_data["case_parties"]
        del post_data["abbr_full_category"]

        text = response.text
        try:
            text_str = json.loads(text)
            text_list = json.loads(text_str)  # I don't know why I need json.loads() twice. ??????

            total_count = int(text_list[0]["Count"])
            print("Count:", total_count)
            # self.result_dict[data["Param"].split(":")[1]] = total_count
            if data["case_parties"] in self.result_dict:
                self.result_dict[data["case_parties"]] += total_count
            else:
                self.result_dict[data["case_parties"]] = total_count
            print(self.result_dict)
            self.output_logger.debug(self.result_dict)
            # self.output_logger.flush()
        except json.JSONDecodeError as jde:
            if "<title>502</title>" in response.text:
                print("The website returns 502")
                time.sleep(10)  # 服务器压力大，休息会儿
            elif "remind" in response.text:
                print("Bad news: the website block the spider")
                time.sleep(10)  # IP代理被禁用了，休息会儿等会儿新的代理
            else:
                print("lxw_JSONDecodeError_NOTE:", jde)
            # 针对这些抓取不成功的case, 重新yield进行抓取
            yield scrapy.FormRequest(url=self.url, formdata=post_data, callback=lambda resp: self.parse(resp, data), dont_filter=True)
        except Exception as e:
            print("lxw_Exception_NOTE:", e)
            # 针对这些抓取不成功的case, 重新yield进行抓取
